import React, { useEffect, useState } from 'react'
import { connect, useSelector } from 'react-redux'

import { fetchPosts } from '../actions/postsActions'

import { Post } from '../components/Post'

const PostsPage = ({ dispatch }) => {
  const getPosts = useSelector((state) => state.posts)

  const [posts, setPosts] = useState([])

  useEffect(() => {
    dispatch(fetchPosts())
  }, [dispatch])

  useEffect(() => {
    console.log('getPosts', getPosts)
    if (getPosts.posts) {
      setPosts(getPosts.posts)
    }
  }, [getPosts])

  const renderPosts = () => {
    // if (getPosts.loading) return <p>Loading posts...</p>
    // if (getPosts.hasErrors) return <p>Unable to display posts.</p>

    return
  }

  return (
    <section>
      <h1>Posts</h1>
      {/* {renderPosts} */}
      {posts?.map((post) => {
        return <Post key={post.id} post={post} excerpt />
      })}
    </section>
  )
}

export default connect()(PostsPage)
