import React, { useEffect,useState } from 'react'
import { connect, useSelector } from 'react-redux'

import { fetchPosts } from '../actions/postsActions'

import { Post } from '../components/Post'

const PostsPage = ({ dispatch }) => {
  const getPosts = useSelector((state) => state.posts)

  // const [posts, setPost] = useState([])

  useEffect(() => {
    dispatch(fetchPosts())
  }, [dispatch])

  useEffect(() => {
    console.log('getPosts', getPosts.posts)
    // if (getPosts.post) {
    //   setPost(getPosts.post)
    // }
  }, [getPosts])

  const renderPosts = () => {
    if (getPosts.loading) return <p>Loading posts...</p>
    if (getPosts.hasErrors) return <p>Unable to display posts.</p>

    return getPosts.posts?.map((post) => <Post key={post.id} post={post} excerpt />)
  }

  return (
    <section>
      <h1>Posts</h1>
      {renderPosts}
    </section>
  )
}

export default connect()(PostsPage)
